var CanReserveDivvy = Class.create();
CanReserveDivvy.prototype = {

   canReserve: function(/*GlideRecord*/ thing, /*GlideDateTime*/ from, /*GlideDateTime*/ until, /*GlideRecord*/ by) {
		if (thing.isValidField("condition"))
			return (thing.getValue("condition") != "1");
		return true;
    },

	handles : function(/*GlideRecord*/ thing) {
		return (thing.getRecordClassName() == "x_bn_bike");
	},

    type: 'CanReserveDivvy'
};
